mapper.select:
select id, name, image, pc_detail_image as pc_image, mobile_detail_image as mobile_image, category, choice_ratio, price
from trims_package
        join package
        on trims_package.package_id = package.id
where trim_id = #{trimId}
order by package_id;

select id, name, image, pc_detail_image as pc_image, mobile_detail_image as mobile_image, category, choice_ratio, price
from trims_package
    join package
        on trims_package.package_id = package.id and
           trims_package.trim_id = #{trimId}
order by package_id

    limit #{pageRange.count}
    offset #{pageRange.from}
